  return (
    <div className="App">
      <h1>Shade Generator</h1>
      <div>
      <Radio.Group onChange={changeType} value={type}>
        <Radio value={1}>HEX</Radio>
        <Radio value={2}>RGB</Radio>
        <Radio value={3}>Text</Radio>
      </Radio.Group>
      </div>
      <div>
      <Input
        prefix="#"
        onChange={genColor}
        placeholder="Enter Hex Code"
        value={color}
        style={{ width: 200 }}
      />
      <Tooltip placement='right' title={color ? "add color" : "enter color"}>
        {" "}
        <Button
          style={{ marginLeft: 10 }}
          type="primary"
          disabled={!color}
          onClick={addColor}
