import React, {useState} from 'react';
import {InputNumber} from 'antd';
import './../App.css'

const RGBInput = (props) =>{

    const {color, setColor} = props;

    const [rgbColor, setRGBColor] = useState({r: null, g: null, b: null})

    const addComma = (text) =>{
        let chunks = [];
        for(let i = 0; i < text.length; i+=3){
          chunks.push(text.substr(i,3));
        }
        return chunks.join();
       }

    const onChange = (e, key) =>{
        debugger
        var myColor = e.target.value.replace(/\W|_|[a-zA-Z]/g, "")
        myColor = addComma(myColor)
        setColor(myColor)
    }

    const {r, g, b} = rgbColor

    return (
        <>
            RGB(
                <InputNumber className='rgbInput' value={r} style={{width: 50}} onChange={(e)=>onChange(e, 'r')}/>
                <InputNumber className='rgbInput' value={g} style={{width: 50}} onChange={(e)=>onChange(e, 'g')}/>
                <InputNumber className='rgbInput' value={b} style={{width: 50, marginRight: 10}} onChange={(e)=>onChange(e, 'b')}/> )
        </>
    )

};

export default RGBInput;
