import React from 'react';
import {Input} from 'antd';

const RGBInput = (props) =>{

    const {color, setColor} = props;

    const addComma = (text) =>{
        let chunks = [];
        for(let i = 0; i < text.length; i+=3){
          chunks.push(text.substr(i,3));
        }
        return chunks.join();
       }

    const onChange = (e) =>{
        var myColor = e.target.value.replace(/\W|_|[a-zA-Z]/g, "")
        myColor = addComma(myColor)
        setColor(myColor)
    }

    return (
        <>
            <Input value={color} style={{width: 200}} prefix="rgb(" onChange={onChange} suffix=")"/>
        </>
    )

};

export default RGBInput;
